    //metodos
    public boolean validarPunto(String textoPR){
        boolean validacion = false;
        for (int i=0; i< textoPR.length(); i++){
            if (textoPR.substring(i, i+1).equals(".")){
                validacion = true;
                break;
            }
        }
        return validacion;
    }

