    private void bPuntoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bPuntoActionPerformed
        String texto = areaEscritura.getText();

        if(texto.length() <= 0){
            //no existe nada en el panel del resultado
            areaEscritura.setText("0.");
        } else {
            //si hay algo en el panel del resultado
            if (!validarPunto(areaEscritura.getText())){
                areaEscritura.setText(areaEscritura.getText()+ ".");
            }
        }

    }//GEN-LAST:event_bPuntoActionPerformed

    private void bIgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bIgualActionPerformed
        String resultado;

        num2 = areaEscritura.getText();

        if (!num2.equals("")){
            resultado = calcular(num1, num2, operador);
            areaEscritura.setText(resultado);
        }
    }//GEN-LAST:event_bIgualActionPerformed

