    private static String calcular(String num1, String num2, String operador){
        double resultado = 0.0;
        String res;

        if(operador.equals("+")){
            //suma
            resultado = Double.parseDouble(num1) + Double.parseDouble(num2);
        } else if (operador.equals("-")){
            //resta
            resultado = Double.parseDouble(num1) - Double.parseDouble(num2);
        } else if (operador.equals("*")){
            //multiplicacion
            resultado = Double.parseDouble(num1) * Double.parseDouble(num2);
        } else if (operador.equals("/")){
            //division
            resultado = Double.parseDouble(num1) / Double.parseDouble(num2);
        }

        res = String.valueOf(resultado);

        return res;
    }

